mapper.select:
SELECT * FROM budget 
WHERE year = #{year}

    AND month = #{month}
    AND month IS NULL


    AND type_id = #{typeId}
    AND type_id IS NULL

SELECT COALESCE(SUM(b.price), 0)
FROM bill b
LEFT JOIN bill_type bt ON b.type_id = bt.id
WHERE YEAR(b.bill_time) = #{year} 
AND MONTH(b.bill_time) = #{month}
AND bt.name != '收入'

SELECT COALESCE(SUM(price), 0)
FROM bill
WHERE YEAR(bill_time) = #{year}
AND MONTH(bill_time) = #{month}
AND type_id = #{typeId}
